import React from "react";
import "./education.css";
import DegreesData from "../education/data.jsx";

function Education() {
  // Extraer los datos y la lógica de expansión de DegreesData
  const { degrees, handleCardClick, expandedIndex } = DegreesData();

  return (
    <div className="education-container" id="education">
      <h2 className="section-title">Education</h2>
      <div className="degree-cards-container">
        {degrees.map((degree, index) => (
          <div
            key={index}
            className={`degree-card ${
              expandedIndex === index ? "expanded" : ""
            }`}
            onClick={() => handleCardClick(index)}
          >
            <img
              src={degree.logo_path}
              alt={degree.alt_name}
              className="degree-logo"
            />
            <h3 className="degree-title">{degree.title}</h3>
            <h4 className="degree-subtitle">{degree.subtitle}</h4>
            {expandedIndex === index && (
              <div className="degree-info">
                {degree.descriptions.map((description, i) => (
                  <p key={i}>{description}</p>
                ))}
                {/* Añadir el enlace del sitio web aquí si lo tienes */}
                <a
                  href={degree.website_link} // Asegúrate de que el enlace exista en tu objeto degree
                  target="_blank"
                  rel="noopener noreferrer"
                ></a>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Education;
