import React from "react";
import "./education.css";
import "bootstrap/dist/css/bootstrap.min.css";

const Education = () => {
  const educationData = [
    {
      year: "2018-2023",
      items: [
        "Técnico en Secretariado Ejecutivo",
        "Asistente Administrativa-Contable"
      ]
    },
    {
      year: "2014-2016",
      items: [
        "Asistente administrativa-inventarios- Prácticas"
      ]
    },
    {
      year: "2014",
      items: [
        "CTP Ricardo Castro Beer"
      ]
    }
  ];

  const certificationsData = [
    {
      year: "2023",
      items: [
        "CENECOOP Contabilidad Básica",
        "IPAI Language School Costa Rica Inglés A2 (en curso)",
        "CAMCOP-Taller de tablas dinámicas"
      ]
    },
    {
      year: "2022",
      items: [
        "Universidad Nacional Costa Rica. Recepcionista",
        "ALCA Solutions Costa Rica Auxiliar de nóminas"
      ]
    },
    {
      year: "2021",
      items: [
        "CENECOOP- Servicio al cliente"
      ]
    }
  ];

  const renderTimelineItem = (data) => {
    return data.map((item, index) => (
      <div key={index} className="row">
        <div className="col-md-2">
          <div className="timeline-year">{item.year}</div>
        </div>
        <div className="col-md-10">
          <div className="timeline-content">
            <ul>
              {item.items.map((subItem, subIndex) => (
                <li key={subIndex}>{subItem}</li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    ));
  };

  return (
    <section className="section pb-0 bg-gradient-info my-5">
      <div className="container">
        <h2 className="section-title text-center text-white mb-5">Historial de Educación y Certificaciones</h2>
        <div className="timeline">
          <div className="timeline-items">
            <h3 className="timeline-heading">Educación</h3>
            {renderTimelineItem(educationData)}
          </div>
          <div className="timeline-items">
            <h3 className="timeline-heading">Cursos y Certificaciones</h3>
            {renderTimelineItem(certificationsData)}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Education;
