import React from "react";
import "./degreecard.css";

const DegreeCard = ({ degree, theme }) => {
  const renderDescriptions = () => {
    return degree.descriptions.map((sentence, index) => (
      <p key={index} className="content-list" style={{ color: theme.text }}>
        {sentence}
      </p>
    ));
  };

  return (
    <div className="degree-card">
      {degree.logo_path && (
        <div className="card-img">
          <img
            className="degree-logo"
            src={require(`../../assests/images/${degree.logo_path}`)}
            alt={degree.alt_name}
          />
        </div>
      )}
      <div className="card-body">
        <div
          className="body-header"
          style={{ backgroundColor: theme.headerColor }}
        >
          <div className="body-header-title">
            <h2 className="card-title" style={{ color: theme.text }}>
              {degree.title}
            </h2>
            <h3 className="card-subtitle" style={{ color: theme.text }}>
              {degree.subtitle}
            </h3>
          </div>
          <div className="body-header-duration">
            <h3 className="duration" style={{ color: theme.text }}>
              {degree.duration}
            </h3>
          </div>
        </div>
        <div className="body-content">{renderDescriptions()}</div>
        {degree.website_link && (
          <a
            href={degree.website_link}
            target="_blank"
            rel="noopener noreferrer"
            className="website-link"
            style={{ color: theme.text }}
          >
            Visit Website
          </a>
        )}
      </div>
    </div>
  );
};

export default DegreeCard;
