import React from "react";
import "./education.css";
import DegreeCard from "../education/degreecard"; // Asegúrate de tener la ruta correcta al componente DegreeCard

const Education = () => {
  // Supongamos que tienes un array de datos de grados
  const degrees = [
    {
      title: "Bachelor's Degree",
      subtitle: "Computer Science",
      duration: "2016 - 2020",
      descriptions: ["Description line 1", "Description line 2"],
      logo_path: "degree_logo.png",
      alt_name: "Degree Alt",
      website_link: "https://www.example.com",
    },
    // Agrega más objetos de grados si es necesario
  ];

  const theme = {
    // Define el tema aquí si es necesario
    headerColor: "#fff",
    text: "#000",
  };

  return (
    <div className="main" id="educations">
      <div className="educations-header-div">
        {/* Puedes agregar encabezados o contenido adicional aquí si es necesario */}
      </div>
      <div className="educations-body-div">
        {degrees.map((degree, index) => (
          <DegreeCard key={index} degree={degree} theme={theme} />
        ))}
      </div>
    </div>
  );
};

export default Education;
