import React from 'react';
import "./degreeCard.css";
import logoUtn from "../../../assets/logoUtn.jpg";


function DegreeCard({ degree }) {
  const { title, subtitle, logoUtn, alt_name, duration, descriptions, website_link } = degree;

  return (
    <div className="degree-card">
      <div className="degree-logo">
        <img src={logoUtn} alt={alt_name} />
      </div>
      <div className="degree-details">
        <h3>{title}</h3>
        <p>{subtitle}</p>
        <p>{duration}</p>
        <ul>
          {descriptions.map((description, index) => (
            <li key={index}>{description}</li>
          ))}
        </ul>
        <a href={website_link} target="_blank" rel="noopener noreferrer">More Info</a>
      </div>
    </div>
  );
}

export default DegreeCard;
