import React from "react";
import "./education.css";
import DegreeCard from "../education/degreecard/DegreeCard";

const Education = () => {
  const degrees = [
    {
      title: "Bachelor's Degree",
      subtitle: "Computer Science",
      duration: "2016 - 2020",
      descriptions: ["Description line 1", "Description line 2"],
      logo_path: "degree_logo.png",
      alt_name: "Degree Alt",
      website_link: "https://www.example.com",
    },
    // Puedes agregar más objetos de grados si es necesario
  ];

  const theme = {
    headerColor: "#3f51b5", // Color de fondo del encabezado
    text: "#000", // Color del texto
  };

  return (
    <div className="main" id="educations">
      <div className="educations-header-div">
        <h2>Mis Grados</h2>
      </div>
      <div className="educations-body-div">
        {degrees.map((degree, index) => (
          <DegreeCard key={index} degree={degree} theme={theme} />
        ))}
      </div>
    </div>
  );
};

export default Education;
