import React, { useState } from "react";
import "./education.css";
import logoUtn from "../../assets/logoUtn.jpg";
import logoCtp from "../../assets/logoCtp.jpg";

function Education() {
  const degrees = [
    {
      title: "Universidad Técnica Nacional",
      subtitle: "Bachelor's degree Business Management Administration",
      logo_path: logoUtn,
      alt_name: "Universidad Técnica Nacional",
      duration: "2018 - 2023",
      descriptions: [
        "⚡ I have studied basic business administration subjects like marketing, project management, cost accounting, occupational safety, business law, business law, etc.",
        "⚡ Apart from this, I have done courses on Deep Learning, Data Science, Cloud Computing and Full Stack Development.",
        "⚡ I was selected for Merit cum Means Scholarship which is given to top 10% of students in college. I have received award from respected director for consistently best performance in academics.",
      ],
      website_link: "http://iiitk.ac.in",
    },
    {
      title: "CTP Ricardo Castro Beer",
      subtitle: "Secretary Executive",
      logo_path: logoCtp,
      alt_name: "CTP Ricardo Castro Beer",
      duration: "2014 - 2016",
      descriptions: [
        "⚡ I have taken a variety of courses related to Artificial Intelligence which correspond to Explainable AI, Graph Machine Learning, Computer Vision etc.",
        "⚡ Apart from this, I have also done research assistantship. As part of it, I have worked on creating new algorithms in Graph ML and Network Science.",
        "⚡ During my time at the university, I was also associated with the multimedia department. As part of it, I have worked on some documentary films and interviews.",
      ],
      website_link: "https://www.indiana.edu/",
    },
  ];

  // Estado para manejar qué tarjeta está expandida
  const [expandedIndex, setExpandedIndex] = useState(null);

  // Función para manejar el clic en una tarjeta
  const handleCardClick = (index) => {
    if (expandedIndex === index) {
      // Si ya está expandida, ciérrala
      setExpandedIndex(null);
    } else {
      // De lo contrario, expande la tarjeta correspondiente
      setExpandedIndex(index);
    }
  };

  return (
    <div className="education-container">
      <h2 className="section-title">Education</h2>
      <div className="degree-cards-container">
        {degrees.map((degree, index) => (
          <div
            key={index}
            className={`degree-card ${
              expandedIndex === index ? "expanded" : ""
            }`}
            onClick={() => handleCardClick(index)}
          >
            <img
              src={degree.logo_path}
              alt={degree.alt_name}
              className="degree-logo"
            />
            <h3 className="degree-title">{degree.title}</h3>
            <h4 className="degree-subtitle">{degree.subtitle}</h4>
            {expandedIndex === index && (
              <div className="degree-info">
                {degree.descriptions.map((description, i) => (
                  <p key={i}>{description}</p>
                ))}
                <a
                  href={degree.website_link}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  More Info
                </a>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Education;
