import React, { useState } from "react";
import "./education.css";

const Education = () => {
  const [accordionItems, setAccordionItems] = useState([
    { id: 1, question: "What are the main features?", answer: "Answer 1" },
    { id: 2, question: "Do I have to pay again after the trial?", answer: "Answer 2" },
    { id: 3, question: "How can I get started after the trial?", answer: "Answer 3" },
    { id: 4, question: "Can I be refunded if I am not satisfied?", answer: "Answer 4" }
  ]);

  const toggleAccordion = (id) => {
    setAccordionItems((prevItems) =>
      prevItems.map((item) =>
        item.id === id ? { ...item, isOpen: !item.isOpen } : item
      )
    );
  };

  return (
    <section id="faq" className="faq">
      <div className="container">
        <div className="row text-center">
          <h1 className="display-3 fw-bold text-uppercase">Education</h1>
          <div className="heading-line"></div>
          <p className="lead">
            Frequently Asked Questions, Get Knowledge Beforehand
          </p>
        </div>
        <div className="row mt-5">
          <div className="col-md-12">
            <div className="accordion" id="accordionExample">
              {accordionItems.map((item) => (
                <div className="accordion-item shadow mb-3" key={item.id}>
                  <h2 className="accordion-header" id={`heading${item.id}`}>
                    <button
                      className={`accordion-button ${item.isOpen ? "" : "collapsed"}`}
                      type="button"
                      onClick={() => toggleAccordion(item.id)}
                    >
                      {item.question}
                    </button>
                  </h2>
                  <div
                    id={`collapse${item.id}`}
                    className={`accordion-collapse collapse ${item.isOpen ? "show" : ""}`}
                    aria-labelledby={`heading${item.id}`}
                    data-bs-parent="#accordionExample"
                  >
                    <div className="accordion-body">{item.answer}</div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Education;
