import React from "react";
import "./education.css";

const Education = () => {
  const educationData = [
    {
      year: "2018-2023",
      items: [
        "Técnico en Secretariado Ejecutivo",
        "Asistente Administrativa-Contable"
      ]
    },
    {
      year: "2014-2016",
      items: [
        "Asistente administrativa-inventarios- Prácticas"
      ]
    },
    {
      year: "2014",
      items: [
        "CTP Ricardo Castro Beer"
      ]
    }
  ];

  const certificationsData = [
    {
      year: "2023",
      items: [
        "CENECOOP Contabilidad Básica",
        "IPAI Language School Costa Rica Inglés A2 (en curso)",
        "CAMCOP-Taller de tablas dinámicas"
      ]
    },
    {
      year: "2022",
      items: [
        "Universidad Nacional Costa Rica. Recepcionista",
        "ALCA Solutions Costa Rica Auxiliar de nóminas"
      ]
    },
    {
      year: "2021",
      items: [
        "CENECOOP- Servicio al cliente"
      ]
    }
  ];

  const renderEducation = (data) => {
    return (
      <ul>
        {data.map((item, index) => (
          <li key={index}>
            <strong>{item.year}</strong>
            <ul>
              {item.items.map((subItem, subIndex) => (
                <li key={subIndex}>{subItem}</li>
              ))}
            </ul>
          </li>
        ))}
      </ul>
    );
  };

  return (
    <section className="section pb-0 bg-gradient-info my-5">
      <div className="container">
        <div className="row justify-content-center">
          <div className="col-lg-6">
            <div className="card bg-gradient-white shadow border-0">
              <div className="card-body">
                <h2 className="card-title text-info mb-3">Educación</h2>
                {renderEducation(educationData)}
              </div>
            </div>
          </div>
          <div className="col-lg-6">
            <div className="card bg-gradient-white shadow border-0">
              <div className="card-body">
                <h2 className="card-title text-info mb-3">Cursos y Certificaciones</h2>
                {renderEducation(certificationsData)}
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Education;
