import React from "react";
import "./education.css";
import { degrees } from "../../portfolio";

function Education(props) {
  const { theme } = props;

  return (
    <section className="education section" id="education">
      <h2 className="section__title">Education</h2>
      <div className="education__container">
        {degrees.degrees.map((degree, index) => (
          <div key={index} className="education__content card">
            {degree.logo_path && (
              <img
                src={logoUTN} alt="" className="education__logo" />
            )}
            <h4 className="education__title">
              <b>{degree.title}:</b> {degree.subtitle}
            </h4>
            <div className="education__group">
              <div className="education__data">
                <span className="bx bx-badge-check"></span>
                <p className="education__name">{degree.institution}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}

export default Education;
