import React from 'react';
import DegreeCard from '../education/DegreeCard/degreeCard'; // Suponiendo que tengas un componente DegreeCard creado
import "./education.css";

function Education() {
  const degrees = [
    {
      title: "Universidad Técnica Nacional",
      subtitle: "Bachelor degree in Adminsitratuve ",
      logo_path: "iiitk_logo.png",
      alt_name: "IIITDM Kurnool",
      duration: "2016 - 2020",
      descriptions: [
        "⚡ I have studied basic software engineering subjects like DS, Algorithms, DBMS, OS, CA, AI etc.",
        "⚡ Apart from this, I have done courses on Deep Learning, Data Science, Cloud Computing and Full Stack Development.",
        "⚡ I was selected for Merit cum Means Scholarship which is given to top 10% of students in college. I have received award from respected director for consistently best performance in academics.",
      ],
      website_link: "http://iiitk.ac.in",
    },
    {
      title: "Indiana University Bloomington",
      subtitle: "M.S. in Computer Science",
      logo_path: "iu_logo.png",
      alt_name: "Indiana University Bloomington",
      duration: "2021 - 2023",
      descriptions: [
        "⚡ I have taken varity of courses related to Artificial Intelligence which correspond to Explainable AI, Graph Machine Learning, Computer Vision etc.",
        "⚡ Apart from this, I have also done research assistantship. As part of it, I have worked on creating new algorithms in Graph ML and Network Science.",
        "⚡ During my time at university, I was also associated with multimedia department. As part of it, I have worked on some documentry films and interviews.",
      ],
      website_link: "https://www.indiana.edu/",
    },
  ];

  return (
    <div className="education-container">
      <h2 className="section__title">Education</h2>
      <div className="degree-cards-container">
        {degrees.map((degree, index) => (
          <DegreeCard key={index} degree={degree} />
        ))}
      </div>
    </div>
  );
}

export default Education;
