import React from "react";
import "./courses.css";
import AboutImg from "../../assets/aboutImage.jpg";

const Courses = () => {
    const certificates = {
        certificate: [
          {
            title: "Machine Learning",
            subtitle: "- Andrew Ng",
            logo_path: "stanford_logo.png",
            certificate_link:
              "https://www.coursera.org/account/accomplishments/verify/22MTSSC5WDTM",
            alt_name: "Stanford University",
            color_code: "#8C151599",
          },
          {
            title: "Deep Learning",
            subtitle: "- Andrew Ng",
            logo_path: "deeplearning_ai_logo.png",
            certificate_link:
              "https://www.coursera.org/account/accomplishments/specialization/H8CPSFXAJD2G",
            alt_name: "deeplearning.ai",
            color_code: "#00000099",
          },
          // Add more certification objects as needed
        ]
      };
      

  return (
    <section className="courses section" id="courses">
      <h2 className="section__title">Courses</h2>

      <div className="courses__container container grid">
        {/* Map through the certificates array to render each certificate */}
        {certificates.map((certificate, index) => (
          <div className="cert-card" key={index}>
            <div className="content">
              <a
                href={certificate.certificate_link}
                target="_blank"
                rel="noopener noreferrer"
              >
                <div className="content-overlay"></div>
                <div
                  className="cert-header"
                  style={{ backgroundColor: certificate.color_code }}
                >
                  <img
                    className="logo_img"
                    src={AboutImg}
                    alt=""
                  />
                </div>
                <div className="content-details fadeIn-top">
                  <h3 className="content-title">Certificate</h3>
                </div>
              </a>
            </div>
            <div className="cert-body">
              <h2 className="cert-body-title">{certificate.title}</h2>
              <h3 className="cert-body-subtitle">{certificate.subtitle}</h3>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Courses;
