import React from "react";
import "./education.css";


const Education = () => {
    return (
        <section className="skills section" id="skills">
            <div class="card">
            <h2 onclick="toggleDetails('education')">Educación</h2>
            <div class="details" id="education">
                <ul>
                    <li>Técnico en Secretariado Ejecutivo - 2018-2023</li>
                    <li>Asistente Administrativa-Contable - 2014-2016</li>
                    <li>Asistente administrativa-inventarios- Prácticas - Universidad Técnica Nacional</li>
                    <li>CTP Ricardo Castro Beer</li>
                </ul>
            </div>
        </div>

        <div class="card">
            <h2 onclick="toggleDetails('certifications')">Certificados</h2>
            <div class="details" id="certifications">
                <ul>
                    <li>CENECOOP Contabilidad Básica (2023)</li>
                    <li>IPAI Language School Costa Rica Inglés A2 (en curso 2023)</li>
                    <li>CAMCOP-Taller de tablas dinámicas 2023</li>
                    <li>Universidad Nacional Costa Rica. Recepcionista 2022</li>
                    <li>ALCA Solutions Costa Rica Auxiliar de nóminas 2022</li>
                    <li>CENECOOP- Servicio al cliente 202</li>
                </ul>
            </div>
        </div>
    
    
    </section>
    

        );
    };
    function toggleDetails(id) {
        var details = document.getElementById(id);
        if (details.style.display === "block") {
            details.style.display = "none";
        } else {
            details.style.display = "block";
        }
    }
    export default Education;
